import { useEffect, useState } from "react";
import axios from "axios";

function ShowEvents() {
  const [events, setEvents] = useState([]);
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");

  const fetcAllEvents = async () => {
    const response = await axios.get("http://localhost:9000/api/schdules");
    if (response.data.success === true) {
      setEvents(response.data.data);
    } else {
      alert(response.data.message);
    }
  };

  useEffect(() => {
    fetcAllEvents();
  }, []);

  const updateEvent = async (id) => {
    const payload = {
      title: title,
      date: date,
      location: location,
    };

    const response = await axios.put(
      "http://localhost:9000/api//updateEvent/" + id,
      payload
    );

    if (response.status === 200) {
      alert("Event updated successfully");
      // Optionally, refresh the event list or update state
    } else {
      alert("Failed to update event");
    }
  };

  const softDeleteEvent = async (id) => {
    const response = await axios.put("http://localhost:9000/api/delete/" + id);

    if (response.status === 200) {
      const newEvents = events.filter((event) => event._id !== id);
      setEvents(newEvents);
    } else {
      alert("Some error while delete the user");
    }
  };

  return (
    <div className="eventlist">
      <h1>Upcoming Schedules</h1>
      {events.map((event) => (
        <div className="events">
          <div className="eventdetails">
            <p>
              <strong>Title:</strong> {event.title}
            </p>
            <p>
              <strong>Date:</strong> {event.date}
            </p>
            <p>
              <strong>Location:</strong> {event.location}
            </p>
          </div>
          <div className="event-actions">
            <button onClick={updateEvent} className="delete-button">
              Update
            </button>
            <button onClick={softDeleteEvent} className="delete-button">
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ShowEvents;
