import { useEffect, useState } from "react";
import axios from "axios";

function ShowEvents() {
  const [events, setEvents] = useState([]);

  const fetcAllEvents = async () => {
    const response = await axios.get("http://localhost:9000/api/schdules");
    if (response.data.success === true) {
      setEvents(response.data.data);
    } else {
      alert(response.data.message);
    }
  };

  useEffect(() => {
    fetcAllEvents();
  }, []);

  const softDelteEvent = async (id) => {
    const response = await axios.put("http://localhost:9000/api/delete/" + id);

    if (response.status === 200) {
      const newEvents = events.filter((EventUser) => EventUser._id !== id);
      setEvents(newEvents);
    } else {
      alert("Some error whilte delete the user");
    }
  };

  return (
    <div>
      <h1>Upcoming Schudles</h1>
      <div className="event-details">
        <p>
          <strong>Title:</strong> {events.title}
        </p>
        <p>
          <strong>Date:</strong> {events.date}
        </p>
        <p>
          <strong>Location:</strong> {events.location}
        </p>
      </div>
      <div className="event-actions">
        {/* <button onClick={() => onUpdate(event)} className="update-button">Update</button> */}
        <button onClick={softDelteEvent} className="delete-button">
          Delete
        </button>
      </div>
    </div>
  );
}

export default ShowEvents;
